mapper.update:
UPDATE product
prod_name = #{prodName} , prod_detail = #{prodDetail}, manufacture_day = #{manuDate} , price = #{price}, image_file = #{fileName}
WHERE prod_no = #{prodNo}

mapper.select:
	  
			SELECT *
FROM (
    SELECT inner_table.*, ROWNUM AS row_seq
    FROM (
        SELECT p.prod_no prod_no, p.prod_name prod_name, p.price price, p.reg_date reg_date, tran_status_code
        FROM product p
        LEFT JOIN transaction t ON p.prod_no = t.prod_no
        
            
                
                    p.prod_no = #{searchKeyword}
                
                
                    p.prod_name = #{searchKeyword}
                
                
                    p.price = #{searchKeyword}
                
            
        
        ORDER BY p.prod_no
    ) inner_table
    WHERE ROWNUM <= #{endRowNum}
)
WHERE row_seq BETWEEN #{startRowNum} AND #{endRowNum}

  	SELECT COUNT(*)
  	FROM(	SELECT  prod_no , prod_name , price, reg_date
					FROM product
					
						
							
					 			prod_no = #{searchKeyword}
							
							
					 			prod_name = #{searchKeyword}
							
							
								price = #{price}
							
						
					 ) countTable
